// GetWithdrawCount get the total number of withdraw
func (k Keeper) GetWithdrawCount(ctx sdk.Context) uint64 {
	store := prefix.NewStore(ctx.KVStore(k.storeKey), types.QbankGlobalKBP)
	byteKey := types.CreateWithdrawCountKey()
	bz := store.Get(byteKey)

	// Count doesn't exist: no element
	if bz == nil {
		return 0
	}

	// Parse bytes
	return binary.BigEndian.Uint64(bz)
}

// SetWithdrawCount set the total number of withdraw
func (k Keeper) SetWithdrawCount(ctx sdk.Context, count uint64) {
	store := prefix.NewStore(ctx.KVStore(k.storeKey), types.QbankGlobalKBP)
	byteKey := types.CreateWithdrawCountKey()
	bz := make([]byte, 8)
	binary.BigEndian.PutUint64(bz, count)
	store.Set(byteKey, bz)
}

// AppendWithdraw appends a withdraw in the store with a new id and update the count
func (k Keeper) AppendWithdraw(
	ctx sdk.Context,
	withdraw types.Withdraw,
) uint64 {
	// Create the withdraw
	count := k.GetWithdrawCount(ctx)

	// Set the ID of the appended value
	withdraw.Id = count

	store := prefix.NewStore(ctx.KVStore(k.storeKey), types.WithdrawKeyKBP)
	appendedValue := k.cdc.MustMarshal(&withdraw)
	store.Set(types.CreateIDKey(withdraw.Id), appendedValue)

	// Update withdraw count
	k.SetWithdrawCount(ctx, count+1)

	return count
}

// SetWithdraw set a specific withdraw in the store
func (k Keeper) SetWithdraw(ctx sdk.Context, withdraw types.Withdraw) {
	store := prefix.NewStore(ctx.KVStore(k.storeKey), types.WithdrawKeyKBP)
	b := k.cdc.MustMarshal(&withdraw)
	store.Set(types.CreateIDKey(withdraw.Id), b)
}

// GetWithdraw returns a withdraw from its id
func (k Keeper) GetWithdraw(ctx sdk.Context, id uint64) (val types.Withdraw, found bool) {
	store := prefix.NewStore(ctx.KVStore(k.storeKey), types.WithdrawKeyKBP)
	b := store.Get(types.CreateIDKey(id))
	if b == nil {
		return val, false
	}
	k.cdc.MustUnmarshal(b, &val)
	return val, true
}

// RemoveWithdraw removes a withdraw from the store
func (k Keeper) RemoveWithdraw(ctx sdk.Context, id uint64) {
	store := prefix.NewStore(ctx.KVStore(k.storeKey), types.WithdrawKeyKBP)
	store.Delete(types.CreateIDKey(id))
}

// GetAllWithdraw returns all withdraw
func (k Keeper) GetAllWithdraw(ctx sdk.Context) (list []types.Withdraw) {
	store := prefix.NewStore(ctx.KVStore(k.storeKey), types.WithdrawKeyKBP)
	iterator := sdk.KVStorePrefixIterator(store, []byte{})

	defer iterator.Close()

	for ; iterator.Valid(); iterator.Next() {
		var val types.Withdraw
		k.cdc.MustUnmarshal(iterator.Value(), &val)
		list = append(list, val)
	}

	return
}

// GetWithdrawIDBytes returns the byte representation of the ID
func GetWithdrawIDBytes(id uint64) []byte {
	bz := make([]byte, 8)
	binary.BigEndian.PutUint64(bz, id)
	return bz
}

// GetWithdrawIDFromBytes returns ID in uint64 format from a byte array
func GetWithdrawIDFromBytes(bz []byte) uint64 {
	return binary.BigEndian.Uint64(bz)
}

// implements the calculation for the withdrawable denom amount of a given user
// on a specific epoch day.
// Logic -
// Get Withdrawable amount from 7 day container.
// Get Withdrawable amount from 21 day container. And so no.
// Sum withdrable amount from all the lockup containers.
// Withdrable key = {Withdrable key prefix} +  "{denom}" + "{uid}""
// Usage - Users withdrable query and tx.

// GetWithdrawableAmt fetch the current withdrable amount of a given user for a given denom
// from the KV store.
// Called for users withdrable query and withdraw tx.
func (k Keeper) GetWithdrawableAmt(ctx sdk.Context, uid, denom string) (coin sdk.Coin) {
	store := prefix.NewStore(ctx.KVStore(k.storeKey), types.WithdrawableKeyKBP)
	key := types.CreateWithdrableKey(denom, uid, "/")
	b := store.Get(key)
	if b == nil {
		return sdk.NewInt64Coin(denom, 0)
	}
	k.cdc.MustUnmarshal(b, &coin)
	return coin
}

// AddWithdrableAmt adds withdrable amount from to the store for a given user and denom.
// Called from the Orion vault end blocker
func (k Keeper) AddWithdrableAmt(ctx sdk.Context, uid string, coin sdk.Coin) {
	store := prefix.NewStore(ctx.KVStore(k.storeKey), types.WithdrawableKeyKBP)
	key := types.CreateWithdrableKey(coin.GetDenom(), uid, "/")
	b := store.Get(key)
	if b == nil {
		b := k.cdc.MustMarshal(&coin)
		store.Set(key, b)
	} else {
		var storedCoin sdk.Coin
		k.cdc.MustUnmarshal(b, &storedCoin)
		storedCoin = storedCoin.Add(coin)
		b = k.cdc.MustMarshal(&storedCoin)
		store.Set(key, b)
	}
}

// SubWithdrableAmt substracts withdrable amount from to the store for a given user and denom.
// Called from the users withdraw transaction processing.
func (k Keeper) SubWithdrableAmt(ctx sdk.Context, uid string, coin sdk.Coin) {
	store := prefix.NewStore(ctx.KVStore(k.storeKey), types.WithdrawableKeyKBP)
	key := types.CreateWithdrableKey(coin.GetDenom(), uid, "/")
	b := store.Get(key)
	if b == nil {
		// Do nothing. Call should never come here.
		// Can panic
	} else {
		var storedCoin sdk.Coin
		k.cdc.MustUnmarshal(b, &storedCoin)
		storedCoin = storedCoin.Sub(coin)
		b = k.cdc.MustMarshal(&storedCoin)
		store.Set(key, b)
	}
}

// GetLockupWithdrawableAmt fetch the current withdrable amount of a given user, denom and lockup period.
// from the lockperiod based KV store container.
func (k Keeper) GetLockupWithdrawableAmt(ctx sdk.Context, uid, denom string, lockupPeriod types.LockupTypes) (coin sdk.Coin) {
	store := prefix.NewStore(ctx.KVStore(k.storeKey), types.WithdrawableKeyKBP)
	key := types.CreateLockupWithdrableKey(denom, uid, lockupPeriod, "/")
	b := store.Get(key)
	if b == nil {
		return sdk.NewInt64Coin(denom, 0)
	}
	k.cdc.MustUnmarshal(b, &coin)
	return coin
}

